import React, { useState } from "react";
import { FormControl, InputGroup, Button,Table} from "react-bootstrap";
import { NavLink, useHistory } from "react-router-dom";


const Products = () => {
  const [inputData, setInputData] = useState([{
    productName: "",
    productPrice: "",
    productQuantity: "",
    productDescription: "",
  }]);
  const [showResults,setShowResults]=useState(false)

  const handleInput = (e) => {
    let name, value;
    name = e.target.name;
    value = e.target.value;
    setInputData([{...inputData,[name]:value}])
  };


  const saveClick=(e)=>{
    console.log(inputData);
      e.preventDefault();
      setShowResults(true)
  }

  return (
    <div className="float-container" style={{padding:"20px"}}>
      <div
        className="float-child text-center"
        style={{ width:"40%",float:"left",padding
        :"20px",marginTop:"90px"}}
      >
        <h3
          className="mb-4"
          style={{
            color: "teal",
            textShadow: "3px 3px 5px #2c2c2c",
            textDecoration: "underline",
          }}
        >
          Enter Your Product Details
        </h3>
        <InputGroup className="mb-3">
          <FormControl
            type="text"
            autoComplete="off"
            placeholder="Product Name"
            name="productName"
            onChange={handleInput}
            value={inputData.productName}
          />
        </InputGroup>
        <InputGroup className="mb-3">
          <FormControl
            type="number"
            autoComplete="off"
            placeholder="Product Price"
            name="productPrice"
            onChange={handleInput}
            value={inputData.productPrice}
          />
        </InputGroup>
        <InputGroup className="mb-3">
          <FormControl
            type="number"
            placeholder="Quantity"
            name="productQuantity"
            onChange={handleInput}
            value={inputData.productQuantity}
          />
        </InputGroup>
        <InputGroup className="mb-3">
          <FormControl
            type="text"
            placeholder="Description"
            name="productDescription"
            onChange={handleInput}
            value={inputData.productDescription}
          />
        </InputGroup>
        <div>
          <Button onClick={saveClick} className="mt-3" variant="primary">
            Save Product
          </Button>
        </div>
      </div>

    { showResults===true? <div className="float-child text-center" style={{ width:"50%",float:"left",padding
        :"20px",marginTop:"90px",marginLeft:"120px"}}> 
        <h3
          className="mb-4"
          style={{
            color: "teal",
            textShadow: "3px 3px 5px #2c2c2c",
            textDecoration: "underline",
          }}
        >
          Your Products
        </h3>

     <Table striped bordered hover variant="dark">
       <thead>
       <tr>
         <th>Id</th>
         <th>Product Name</th>
         <th>Price</th>
         <th>Quantity</th>
         <th>Description</th>
         </tr>
       </thead>
       <tbody>
         {
           inputData.map((product)=>{
             return(
               <tr key={product.id}>
                 <td>{product.id}</td>
                 <td>{product.productName}</td>
                 <td>{product.productPrice}</td>
                 <td>{product.productQuantity}</td>
                 <td>{product.productDescription}</td>
               </tr>
             )
           })
         }
       </tbody>

     </Table>
     </div> : null}
    </div>
  );
};

export default Products;
