import React, { useState } from "react";
import { FormControl, InputGroup, Button,Table} from "react-bootstrap";
import { NavLink, useHistory } from "react-router-dom";


const Products = () => {
  const [inputData, setInputData] = useState({
    productName: "",
    productPrice: "",
    productQuantity: "",
    productDescription: "",
  });

  const handleInput = (e) => {
    let name, value;
    name = e.target.name;
    value = e.target.value;
    setInputData({...inputData,[name]:value})
  };

  console.log(inputData);

  return (
    <div className="float-container" style={{padding:"20px"}}>
      <div
        className="float-child text-center"
        style={{ width:"50%",float:"left",padding
        :"20px"}}
      >
        <h3
          className="mb-4"
          style={{
            color: "teal",
            textShadow: "3px 3px 5px #2c2c2c",
            textDecoration: "underline",
          }}
        >
          Enter Your Product Details
        </h3>
        <InputGroup className="mb-3">
          <FormControl
            type="text"
            autoComplete="off"
            placeholder="Product Name"
            name="productName"
            onChange={handleInput}
            value={inputData.productName}
          />
        </InputGroup>
        <InputGroup className="mb-3">
          <FormControl
            type="number"
            autoComplete="off"
            placeholder="Product Price"
            name="productPrice"
            onChange={handleInput}
            value={inputData.productPrice}
          />
        </InputGroup>
        <InputGroup className="mb-3">
          <FormControl
            type="number"
            placeholder="Quantity"
            name="productQuantity"
            onChange={handleInput}
            value={inputData.productQuantity}
          />
        </InputGroup>
        <InputGroup className="mb-3">
          <FormControl
            type="text"
            placeholder="Description"
            name="productDescription"
            onChange={handleInput}
            value={inputData.productDescription}
          />
        </InputGroup>
        <div>
          <Button  className="mt-3" variant="primary">
            Save
          </Button>
        </div>
      </div>

     <div className="float-child text-center" style={{ width: "400px", marginTop: "150px",marginRight:"100px" }}> 
     <Table>
       <thead>
         <td>Id</td>
         <td>Product Name</td>
         <td>Price</td>
         <td>Quantity</td>
         <td>Description</td>
       </thead>
     </Table>
     </div>
    </div>
  );
};

export default Products;
